	 *
	 * @param      Criteria $query Criteria to filter results. 
	 * @param      PropelPDO $con Connection to use.
	 * @return     array 		List of sfNestedComment objects
	 */
	public function getBranch($query = null, PropelPDO $con = null)
	{
		return sfNestedCommentQuery::create(null, $query)
			->branchOf($this)
			->orderByBranch()
			->find($con);
	}
	
	/**
	 * Gets ancestors for the given node, starting with the root node
	 * Use it for breadcrumb paths for instance
	 *
	 * @param      Criteria $query Criteria to filter results. 
	 * @param      PropelPDO $con Connection to use.
	 * @return     array 		List of sfNestedComment objects
	 */
	public function getAncestors($query = null, PropelPDO $con = null)
	{
		if($this->isRoot()) {
			// save one query
			return array();
		} else {
			return sfNestedCommentQuery::create(null, $query)
				->ancestorsOf($this)
				->orderByBranch()
				->find($con);
		}
	}
	
	/**
	 * Inserts the given $child node as first child of current
	 * The modifications in the current object and the tree
	 * are not persisted until the child object is saved.
	 *
	 * @param      sfNestedComment $child	Propel object for child node
	 *
	 * @return     sfNestedComment The current Propel object
	 */
	public function addChild(sfNestedComment $child)
	{
		if ($this->isNew()) {
			throw new PropelException('A sfNestedComment object must not be new to accept children.');
		}
		$child->insertAsFirstChildOf($this);
		return $this;
	}
	
	/**
	 * Inserts the current node as first child of given $parent node
	 * The modifications in the current object and the tree
	 * are not persisted until the current object is saved.
	 *
	 * @param      sfNestedComment $parent	Propel object for parent node
	 *
	 * @return     sfNestedComment The current Propel object
	 */
	public function insertAsFirstChildOf($parent)
	{
		if ($this->isInTree()) {
			throw new PropelException('A sfNestedComment object must not already be in the tree to be inserted. Use the moveToFirstChildOf() instead.');
		}
		$left = $parent->getLeftValue() + 1;
		// Update node properties
		$this->setLeftValue($left);
		$this->setRightValue($left + 1);
		$this->setLevel($parent->getLevel() + 1);
		$scope = $parent->getScopeValue();
		$this->setScopeValue($scope);
		// update the children collection of the parent
		$parent->addNestedSetChild($this);
		
		// Keep the tree modification query for the save() transaction
		$this->nestedSetQueries []= array(
			'callable'  => array('sfNestedCommentPeer', 'makeRoomForLeaf'),
			'arguments' => array($left, $scope)
		);
		return $this;
	}
	
